
import Scope from './scope';

const prefixes = [
  '-webkit',
  '-moz',
  '-o'
];

export default class Animation extends Scope {

  constructor(name, serializePrefixes=true){
    super();
    this.name = name;
    this.serializePrefixes = serializePrefixes;
  }

  serialize(){
    let css = `@keyframes ${this.name}{${super()}}`;

    if (this.serializePrefixes) {
      prefixes.forEach(p => {
        css += `@${p}-keyframes ${this.name}{${super()}}`;
      });
    }

    return css;
  }

}
