
import Block from './block';

export default class Ruleset extends Block {

  constructor(sel, decs){
    this.sel = sel;
    this.decs = decs;
  }

  serialize(){
    let decs = this.decs;
    var str = '';
    for (let key in decs) {
      if (str.length) str += ';';
      str += `${key}:${decs[key]}`;
    }
    return `${this.sel}{${str}}`;
  }

}
