
import Block from './block';
import Ruleset from './ruleset';

export default class Scope extends Block {

  constructor(){
    this.children = [];
  }

  add(sel, decs){
    if (sel instanceof Block) {
      this.children.push(sel);
    } else {
      let rule = new Ruleset(sel, decs);
      this.children.push(rule);
    }
    return this;
  }

  remove(dec){
    this.children = this.children.filter((v) => {
      return dec == v;
    });
  }

  serialize(){
    return this.children
    .map(child => child.serialize())
    .join('');
  }

}
