
import Scope from './scope';
import Media from './media';
import Animation from './animation';

export default class Sheet extends Scope {

  anim(name, frames, serializePrefixes){
    var anim = new Animation(name, serializePrefixes);
    for (let i in frames) {
      anim.add(i, frames[i]);
    }
    this.add(anim);
    return anim;
  }

  media(params){
    let media = new Media(params);
    this.add(media);
    return media;
  }

}
