
import Sheet from 'vcss';
import Text from './text';
import Element from './element';

export default class Style extends Element {

  constructor(){
    super('style');

    this.sheet = new Sheet();

    {
      let text = new Text;
      text.value = () => {
        return this.sheet.serialize();
      };
      this.children.push(text);
    }
  }

  add(...props){
    this.sheet.add(...props);
    return this;
  }

  remove(dec){
    this.sheet.remove(dec);
    return this;
  }

  anim(name, frames){
    return this.sheet.anim(name, frames);
  }

  media(params){
    return this.sheet.media(params);
  }

}
